t.script:
setTimeout(function() {
    document.getElementById("loading_ajax_icon").style.display = "none";
    if(location.pathname.substring(0, 5) == "/web" || 
        location.pathname.substring(0, 8) == "/es/web" || 
        location.pathname.substring(0, 5) == "/web/" || 
        location.pathname.substring(0, 8) == "/es/web/" || 
        location.pathname.substring(0, 5) == "/web?" || 
        location.pathname.substring(0, 8) == "/es/web?")
    {
        document.getElementById("translate_text_latinchain_2").style.display = "none";
    }
}, 5000);
window.addEventListener('load', function() {
    document.getElementById("loading_ajax_icon").style.display = "none";
    if(location.pathname.substring(0, 5) == "/web" || 
        location.pathname.substring(0, 8) == "/es/web" || 
        location.pathname.substring(0, 5) == "/web/" || 
        location.pathname.substring(0, 8) == "/es/web/" || 
        location.pathname.substring(0, 5) == "/web?" || 
        location.pathname.substring(0, 8) == "/es/web?")
    {
        document.getElementById("translate_text_latinchain_2").style.display = "none";
    }
});

setTimeout(function() {
    document.getElementById("loading_ajax_icon").style.display = "none";
    if(location.pathname.substring(0, 5) == "/web" || 
        location.pathname.substring(0, 8) == "/es/web" || 
        location.pathname.substring(0, 5) == "/web/" || 
        location.pathname.substring(0, 8) == "/es/web/" || 
        location.pathname.substring(0, 5) == "/web?" || 
        location.pathname.substring(0, 8) == "/es/web?")
    {
        document.getElementById("translate_text_latinchain_2").style.display = "none";
    }else
        document.getElementById("translate_text_latinchain_2").style.display = "";
}, 5000);
